import {Component} from '@angular/core'

@Component({
  selector: "custom-button",
  templateUrl: "./button.component.html",
  styleUrls: ['./button.component.css']
})

export class ButtonComponent {
  changeIcon(count:number) {
    setTimeout(() => {
      return count ++
    }, 3000);
  }

  count = 0
}
<div class="content">
  <button (click)="changeIcon(count)" class="button fa-solid fa-house">
    {{count}}
  </button>
</div>
