import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'angular app';
}
<style>
  .wrapper {
    width: 100vw;
    height: 100vh;
  }

  .title {
    text-align: center;
  }
</style>

<div class="wrapper">
  <h1 class="title">{{ title | titlecase }}</h1>
  <custom-button />
</div>
